mapper.sql:
WHERE deleted = 0

    AND org_id = #{org.org_id}


    AND top_id = #{org.top_id}


    AND parent_id = #{org.parent_id}


    AND org_level = #{org.org_level}


    AND org_name = #{org.org_name}


    AND enabled = #{org.enabled}


    AND updater = #{org.updater}


    AND creator = #{org.creator}


    AND update_time = #{org.update_time}


    AND create_time = #{org.create_time}
