mapper.sql:
WHERE r.deleted = 0

    AND r.role_id = #{dto.roleId}


    AND r.org_id = #{dto.orgId}


    AND o.org_name LIKE CONCAT(#{dto.orgName},'%')


    AND r.top_id = #{dto.topId}


    AND r.updater = #{dto.updater}


    AND uu.user_name LIKE CONCAT(#{dto.updaterName},'%')


    AND r.creator = #{dto.creator}


    AND cu.user_name LIKE CONCAT(#{dto.creatorName},'%')


    AND r.create_time >= #{dto.createTimeStart}


    AND r.create_time <= #{dto.createTimeEnd}

mapper.select:
SELECT r.role_id, r.org_id, o.org_name, r.top_id, r.updater, uu.user_name AS updater_name, r.creator, cu.user_name AS creator_name, r.update_time, r.create_time
FROM works.sys_role AS r JOIN works.sys_user AS cu ON (r.creator = cu.user_id) JOIN works.sys_organization AS o ON (r.org_id = o.org_id) JOIN works.sys_user AS uu ON (r.updater = uu.user_id)

LIMIT #{page.pos}, #{page.range}
